import React, { Component } from 'react';
import { StyleSheet, Text, View, Image, Button, Platform, SafeAreaView, Alert} from 'react-native';

class President extends Component {
    constructor(props){
        super(props);
        this.state = {
            path: require("./assets/favicon.png")
        }
    }

    changeImage = () => {
        this.setState({path: require("./assets/splash.png")})
    }

    render(){
        return (
            <View>
                <Image
                    source={this.state.path}
                    style = {{height:200, width:200}}
                />
                <Button title="Change Image Randomly" onPress={this.changeImage}></Button>
            </View>
        );
    }
}

export default President;
