import React, { useState } from 'react';
import {View, Image, Button} from 'react-native';

const President = (props) => {
    let imgURL = "./assets/{img}";
    const [path, setPath] = useState("./assets/favicon.png");

    const changeImage = () => {
        let library = ["favicon.png", "icon.png", "splash.png"];
        let index = Math.floor(Math.random() * library.length);
        imgURL = imgURL.replace('{img}', library[index]);
        setPath(imgURL);
    }

    return (
        <View>
            <Image
                source={require(path)}
                style = {{height:200, width:200}}
            />
            <Button title="Change Image Randomly" onPress={changeImage}></Button>
        </View>
    );
}

export default President;
