import React, { useState } from 'react';
import {View, Picker, Text, TextInput, TouchableOpacity} from 'react-native';

const CurrencyConverter = (props) => {

    const [apiUnitResponse, setApiUnitResponse] = useState({});
    const [fromUnit, setFromUnit] = useState("CAD");
    const [toUnit, setToUnit] = useState("CAD");
    const [fromValue, setFromValue] = useState('1');
    const [toValue, setToValue] = useState('1');
    return (
        <View>
            <Text>Choose the currency and the amounts to get the exchange rate</Text>
            <Picker
                selectedValue={fromUnit}
                onValueChange={(itemValue, itemIndex) => {
                    fetch(`https://api.exchangerate-api.com/v4/latest/${itemValue}`)
                    .then((response) => {
                        return response.json();
                    })
                    .then((data) => {
                        setApiUnitResponse(data.rates);
                    })
                    setFromUnit(itemValue);
                }}
            >
                <Picker.Item label="CAD" value="CAD" />
                <Picker.Item label="USD" value="USD" />
                <Picker.Item label="EUR" value="EUR" />
                <Picker.Item label="GBP" value="GBP" />
            </Picker>
            <TextInput
                onChangeText={text => setFromValue(text)}
                value={fromValue}
                keyboardType={'numeric'}
            />
            <Text>1 CAD = 0.748743 USD</Text>
            <TouchableOpacity>
                <Text>Swap</Text>
            </TouchableOpacity>
            <Picker
                selectedValue={toUnit}
                onValueChange={(itemValue, itemIndex) => {
                    setToValue(fromValue * apiUnitResponse[itemValue]);
                    setToUnit(itemValue);
                }}
            >
                <Picker.Item label="CAD" value="CAD" />
                <Picker.Item label="USD" value="USD" />
                <Picker.Item label="EUR" value="EUR" />
                <Picker.Item label="GBP" value="GBP" />
            </Picker>
            <TextInput
                onChangeText={text => setToValue(text)}
                value={toValue}
                keyboardType={'numeric'}
            />
        </View>
    );
}

export default CurrencyConverter;
